import React, { Component, useState, useEffect } from "react";
import Web3 from "web3";
import Kycabi from "../../contracts/kyc.json";

export default function BankAuth(props) {
  const [bank, setBank] = useState({
    bankName: "",
    password: "",
  });

  const [submitted, setSubmitted] = useState(false);

  let name, value;

  const getBankData = (event) => {
    name = event.target.name;
    value = event.target.value;

    setBank({
      ...bank,
      [name]: value,
    });
  };

  const signUp = async (bankName, password) => {
    const web3 = window.web3;
    const acc = web3.currentProvider.selectedAddress;
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    if (networkData) {
      const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);
      // kyc.methods.addBank.call(bankName, password, acc, 0);
      // window.alert("Bank added successfully");
      kyc.metthods.checkBank(bankName, password);
      //   if (kyc.metthods.checkBank(bankName, password, acc) == null)
      //     window.alert("Invalid");
      //   else window.alert("Bank added successfully");
      // } else {
      //   window.alert("Kyc contract not deployed to detected network.");
    }
  };

  const onSubmit = (event) => {
    const { bankName, password } = bank;
    event.preventDefault();
    setSubmitted(true);
    signUp(bankName, password);
  };

  return (
    <div>
      <br />
      <div class="form-container">
        <form class="register-form">
          {submitted ? (
            <div class="success-message">
              Success! Thank you for registering
            </div>
          ) : null}
          <input
            value={bank.bankName}
            id="bank-name"
            class="form-field"
            type="text"
            placeholder="Bank name"
            name="bankName"
            onChange={getBankData}
          />
          {/* <span id="user-name-error">Please enter a first name</span> */}
          <input
            value={bank.password}
            id="password"
            class="form-field"
            type="text"
            placeholder="Password"
            name="password"
            onChange={getBankData}
          />
          <button class="form-field" type="submit" onClick={onSubmit}>
            Register
          </button>
        </form>
      </div>
    </div>
  );
}
