import React, { useState } from "react";
import {
  Grid,
  Paper,
  Avatar,
  TextField,
  Button,
  Typography,
  Link,
} from "@material-ui/core";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
import Kycabi from "../../contracts/kyc.json";

const Login = ({ handleChange }) => {
  const paperStyle = {
    padding: 20,
    width: 500,
    margin: "0 auto",
  };

  const avatarStyle = { backgroundColor: "#1bbd7e" };
  const btnstyle = { margin: "8px 0" };
  const web3 = window.web3;
  const acc = web3.currentProvider.selectedAddress;

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onClickLogin = async (bankName, password) => {
    const web3 = window.web3;
    const acc = web3.currentProvider.selectedAddress;
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    if (networkData) {
      const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);
      // try {
      //   console.log(bankName, password, acc);
      //   console.log(
      //     await kyc.methods.addBank(bankName, password, acc).send({
      //       from: acc,
      //       gas: 470000,
      //     })
      //   );
      //   window.alert("Bank added successfully");
      // } catch (e) {
      //   window.alert(e);
      // }
      console.log(username, password);
    } else {
      window.alert("Kyc contract not deployed to detected network.");
    }
  };

  return (
    <Grid>
      <Paper style={paperStyle}>
        <Grid align="center">
          <Avatar style={avatarStyle}>
            <LockOutlinedIcon />
          </Avatar>
          <h2>Sign In</h2>
        </Grid>
        <input
          value={bank.bankName}
          id="bank-name"
          class="form-field"
          type="text"
          placeholder="Bank name"
          name="bankName"
          onChange={getBankData}
        />
        {/* <span id="user-name-error">Please enter a first name</span> */}
        <input
          value={bank.password}
          id="password"
          class="form-field"
          type="text"
          placeholder="Password"
          name="password"
          onChange={getBankData}
        />
        <br />
        <br />
        <Typography>You are signing up using account: </Typography>
        <Typography>{acc}</Typography>
        <br />
        <Button
          type="submit"
          color="primary"
          variant="contained"
          style={btnstyle}
          fullWidth
          onClick={onClickLogin}
        >
          Sign in
        </Button>
        <Typography>
          {" "}
          Do you have an account ?
          <Link href="#" onClick={() => handleChange("event", 1)}>
            Sign Up
          </Link>
        </Typography>
      </Paper>
    </Grid>
  );
};

export default Login;
