  //   const networkId = await web3.eth.net.getId();
  //   const networkData = Kycabi.networks[networkId];
  //   if (networkData) {
  //     console.log("success");
  //     var acc = web3.currentProvider.selectedAddress;
  //     console.log(acc);
  //   } else {
  //     window.alert("Kyc contract not deployed to detected network.");
  //   }
  // }

  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <Navbar />

          <Routes>
            <Route path="/bank_home/add_kyc" element={<BankHome />} />
            <Route path="/bank_home/view_kyc" element={<ViewKyc />} />
            <Route path="/bank_home/modify_kyc" element={<ModifyKyc />} />
            <Route
              path="/bank_home/bank_details"
              element={<BankDetails bankdetails={bankdetails} />}
            />
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
