import React, { useState } from "react";
import ViewCustomer from "./ViewCustomer";
import Kycabi from "../../contracts/kyc.json";

function ViewKyc() {
  const [username, updateUsername] = useState("");
  const [permission, updatePermission] = useState(false);
  const web3 = window.web3;
  const acc = web3.currentProvider.selectedAddress;

  let CustomerDetails = {
    username: "ram123",
    firstname: "Ram",
    lastname: "Surya",
    dob: "18/May/1998",
    phone: "9876543210",
    email: "ramsurya@gmail.com",
    address: "Bengaluru, KA, 560068",
    pan: "DQUIR9875F",
  };

  function getDetails() {
    CustomerDetails = {
      username: "ram123",
      firstname: "Ram",
      lastname: "Surya",
      dob: "18/May/1998",
      phone: "9876543210",
      email: "ramsurya@gmail.com",
      address: "Bengaluru, KA, 560068",
      pan: "DQUIR9875F",
    };
  }
  const handleOnSubmit = async () => {
    //TODO: check permission

    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    console.log(
      await kyc.methods.viewCustomer(username).call({ from: acc, gas: 4700000 })
    );
    const perm = true;
    if (perm) {
      updatePermission(perm);
      getDetails();
    } else {
      alert("Please take the permission from the customer");
    }
  };
  return (
    <div
      style={{
        alignItems: "center",
        margin: "auto",
      }}
    >
      <br />
      <h1
        style={{
          marginLeft: "20px",
        }}
      >
        VIEW KYC
      </h1>
      <br />
      {!permission ? (
        <div>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              handleOnSubmit();
            }}
          >
            <div>
              <label htmlFor="username">
                {"Customer Username :  "}
                <input
                  id="username"
                  placeholder="username"
                  type="text"
                  value={username}
                  onChange={(e) => updateUsername(e.target.value)}
                />
              </label>
            </div>
            <button>Search</button>
          </form>
        </div>
      ) : (
        <ViewCustomer CustomerDetails={CustomerDetails} />
      )}
    </div>
  );
}

export default ViewKyc;
