import React from "react";

export default function ModifyCustomer(props) {
  const onClickModify = async (event) => {
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    const {
      username,
      firstName,
      lastName,
      dob,
      phone,
      email,
      address,
      aadharNo,
      panNo,
    } = user;
    event.preventDefault();
  };

  const [submitted, setSubmitted] = useState(false);

  return (
    <div class="form-container register-form">
      {submitted ? (
        <div class="success-message">Success! Thank you for registering</div>
      ) : null}
      <input
        value={user.username}
        id="user-name"
        class="form-field"
        type="text"
        placeholder="Username"
        name="username"
        onChange={getUserData}
      />
      <input
        value={user.firstName}
        id="first-name"
        class="form-field"
        type="text"
        placeholder="First Name"
        name="firstName"
        onChange={getUserData}
      />
      <input
        value={user.lastName}
        id="last-name"
        class="form-field"
        type="text"
        placeholder="Last Name"
        name="lastName"
        onChange={getUserData}
      />
      <input
        value={user.dob}
        id="dob"
        class="form-field"
        type="text"
        placeholder="DOB"
        name="dob"
        onChange={getUserData}
      />
      <input
        value={user.phone}
        id="phone"
        class="form-field"
        type="number"
        placeholder="Phone"
        name="phone"
        onChange={getUserData}
      />
      <input
        value={user.email}
        id="email"
        class="form-field"
        type="email"
        placeholder="Email"
        name="email"
        onChange={getUserData}
      />
      <input
        value={user.address}
        id="address"
        class="form-field"
        type="text"
        placeholder="address"
        name="address"
        onChange={getUserData}
      />
      <input
        value={user.aadharNo}
        id="aadharNo"
        class="form-field"
        type="number"
        placeholder="Aadhar Number"
        name="aadharNo"
        onChange={getUserData}
      />
      <input
        value={user.panNo}
        id="panNo"
        class="form-field"
        type="text"
        placeholder="PAN number"
        name="panNo"
        onChange={getUserData}
      />
      <button class="form-field" type="submit" onClick={onClickModify}>
        Register
      </button>
    </div>
  );
}
