import React, { useState } from "react";
import {
  Grid,
  Paper,
  Avatar,
  TextField,
  Button,
  Typography,
  Link,
} from "@material-ui/core";
import LockOutlinedIcon from "@material-ui/icons/LockOutlined";

const Login = ({ handleChange }) => {
  const paperStyle = {
    padding: 20,
    width: 500,
    margin: "0 auto",
  };

  const avatarStyle = { backgroundColor: "#1bbd7e" };
  const btnstyle = { margin: "8px 0" };
  const web3 = window.web3;
  const acc = web3.currentProvider.selectedAddress;

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onClickLogin = async (bankName, password) => {
    const web3 = window.web3;
    const acc = web3.currentProvider.selectedAddress;
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    if (networkData) {
      const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);
      // try {
      //   console.log(bankName, password, acc);
      //   console.log(
      //     await kyc.methods.addBank(bankName, password, acc).send({
      //       from: acc,
      //       gas: 470000,
      //     })
      //   );
      //   window.alert("Bank added successfully");
      // } catch (e) {
      //   console.log(e);
      // }
      console.log(username, password);
    } else {
      window.alert("Kyc contract not deployed to detected network.");
    }
  };

  return (
    <Grid>
      <Paper style={paperStyle}>
        <Grid align="center">
          <Avatar style={avatarStyle}>
            <LockOutlinedIcon />
          </Avatar>
          <h2>Sign In</h2>
        </Grid>
        <form>
          <TextField
            label="Username"
            placeholder="Enter username"
            fullWidth
            required
            value={username}
            onChange={(e) => {
              setUsername(e.target.value);
            }}
          />
          <TextField
            label="Password"
            placeholder="Enter password"
            type="password"
            fullWidth
            required
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
          <br />
          <br />
          <Typography>You are signing up using account: </Typography>
          <Typography>{acc}</Typography>
          <br />
          <Button
            type="submit"
            color="primary"
            variant="contained"
            style={btnstyle}
            fullWidth
          >
            Sign in
          </Button>
          <Typography>
            {" "}
            Do you have an account ?
            <Link href="#" onClick={() => handleChange("event", 1)}>
              Sign Up
            </Link>
          </Typography>
        </form>
      </Paper>
    </Grid>
  );
};

export default Login;
