import React, { Component, useState } from "react";
import Kycabi from "../../contracts/kyc.json";
import ModifyCustomer from "./ModifyCustomer";

export default function ModifyKyc() {
  const [permission, updatePermission] = useState(false);
  const [customerDetails, updatecustomerDetails] = useState([]);
  const web3 = window.web3;
  const acc = web3.currentProvider.selectedAddress;
  const [username, updateUsername] = useState("");

  const update = () => {
    user.username = customerDetails[0];
    user.firstName = customerDetails[1];
    user.lastName = customerDetails[2];
    user.dob = customerDetails[3];
    user.phone = customerDetails[4];
    user.email = customerDetails[5];
    user.address = customerDetails[6];
    user.aadharNo = customerDetails[7];
    user.panNo = customerDetails[8];
  };

  let name, value;
  const getUserData = (event) => {
    name = event.target.name;
    value = event.target.value;

    setUser({
      ...user,
      [name]: value,
    });
  };

  const OnClickSearch = async (event) => {
    event.preventDefault();
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    let a = await kyc.methods
      .viewCustomer(username)
      .call({ from: acc, gas: 4700000 });

    if (a == "") {
      window.alert("Customer data not found");
      return;
    } else {
      updatecustomerDetails(a.split("!@#"));
      console.log(customerDetails[0]);
      updatePermission(true);
    }
  };

  const [user, setUser] = useState({
    username: "",
    firstName: "",
    lastName: "",
    dob: "",
    phone: "",
    email: "",
    address: "",
    aadharNo: "",
    panNo: "",
  });

  const onClickModify = async (event) => {
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    const {
      username,
      firstName,
      lastName,
      dob,
      phone,
      email,
      address,
      aadharNo,
      panNo,
    } = user;
    event.preventDefault();
  };

  const [submitted, setSubmitted] = useState(false);

  return (
    <div>
      <br />
      <h1>Modify kyc</h1>
      <br />
      {!permission ? (
        <div class="form-container register-form">
          <input
            class="form-field"
            id="username"
            placeholder="Enter username to search"
            type="text"
            value={username}
            onChange={(e) => updateUsername(e.target.value)}
            name="username"
          />
          <button class="form-field" type="submit" onClick={OnClickSearch}>
            Search
          </button>
        </div>
      ) : (
        <ModifyCustomer />
      )}
    </div>
  );
}
