import { React, Component, useState } from "react";
import Kycabi from "../../contracts/kyc.json";

let acc;
let [name, setName] = useState("");
let [count, setCount] = useState("");

const getDetails = async () => {
  acc = web3.currentProvider.selectedAddress;
  const networkId = web3.eth.net.getId();
  const networkData = Kycabi.networks[networkId];
  const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);
  let details = await kyc.methods.viewBank(acc).call({
    from: acc,
    gas: 4700000,
  });
  setName(details[0]);
  setCount(details[1]);
  console.log(name);
};

getDetails();

export default function BankDetails() {
  return (
    <div
      style={{
        marginLeft: "20px",
      }}
    >
      <div>
        <h3>Bank name : {name}</h3>
      </div>
      <div>
        <h3>ETH Address : {web3.currentProvider.selectedAddress}</h3>
      </div>
      <div>
        <h3>Total KYC count : {count}</h3>
      </div>
    </div>
  );
}

