import { BrowserRouter, Routes, Route } from "react-router-dom";
import { React, Component } from "react";
import Web3 from "web3";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import SignInOutContainer from "./components/auth/container";
import BankHome from "./components/bank_home/bank_home";
import { BankApp } from "./components/bank_home/bank_app";

class App extends Component {
  async componentDidMount() {
    await this.loadWeb3();
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  render() {
    return (
      <BrowserRouter>
        <div className="App">
          <Routes>
            <Route path="/" element={<SignInOutContainer />} />
            <Route path="/bank_home/bank_app" element={<BankApp />} />
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
