import AcceptReject from "./AcceptReject";
import { useState } from "react";
import Navbar from "../navbar/Navbar";

function Request() {
  const [bankRequests, updateBankRequest] = useState([
    { id: 1, name: "HDFC", accept: false, reject: false },
    { id: 2, name: "SBI", accept: false, reject: false },
    { id: 3, name: "Citi Bank", accept: false, reject: false },
    { id: 4, name: "Karnataka Bank", accept: false, reject: false },
  ]);

  function onAccept(index) {
    console.log("Accept " + index + " " + bankRequests[index].name);
    const editedBank = bankRequests[index];
    editedBank.accept = true;
    console.log(editedBank);
    const id = editedBank.id;
    var newList = bankRequests.filter((bank) => bank.id !== id);
    newList.concat(editedBank);
    updateBankRequest(newList);
    console.log(bankRequests);
  }
  function onReject(index) {
    console.log("Reject " + index + " " + bankRequests[index].name);
    const editedBank = bankRequests[index];
    editedBank.reject = true;
    console.log(editedBank);
    const id = editedBank.id;
    var newList = bankRequests.filter((bank) => bank.id !== id);
    newList.concat(editedBank);
    updateBankRequest(newList);
    console.log(bankRequests);
  }

  return (
    <div className="App">
      <Navbar />
      <br />
      <h3>New Requests</h3>
      {bankRequests.map((request, index) => {
        if (!request.accept && !request.reject) {
          return (
            <AcceptReject
              request={request}
              index={index}
              onAccept={onAccept}
              onReject={onReject}
            />
          );
        } else return null;
      })}
    </div>
  );
}

export default Request;

