import React, { Component, useState } from "react";
import Kycabi from "../../contracts/kyc.json";

export default function ModifyKyc() {
  const [permission, updatePermission] = useState(false);
  const web3 = window.web3;
  const acc = web3.currentProvider.selectedAddress;
  const [username, updateUsername] = useState("");

  const [user, setUser] = useState({
    username: "",
    firstName: "",
    lastName: "",
    dob: "",
    phone: "",
    email: "",
    address: "",
    aadharNo: "",
    panNo: "",
  });

  let name, value;
  const getUserData = (event) => {
    name = event.target.name;
    value = event.target.value;

    setUser({
      ...user,
      [name]: value,
    });
  };

  const handleOnSubmit = async (event) => {
    event.preventDefault();
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    let a = await kyc.methods
      .viewCustomer(username)
      .call({ from: acc, gas: 4700000 });
    if (a == "") {
      window.alert("Customer data not found");
      return;
    } else {
      updatePermission(true);
    }
  };

  const onClick = async (event) => {
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);

    const {
      username,
      firstName,
      lastName,
      dob,
      phone,
      email,
      address,
      aadharNo,
      panNo,
    } = user;
    event.preventDefault();
  };

  const [submitted, setSubmitted] = useState(false);

  return (
    <div>
      <br />
      <h1>Modify kyc</h1>
      <br />
      {!permission ? (
        <div class="form-container register-form">
          <input
            class="form-field"
            id="username"
            placeholder="Enter username to search"
            type="text"
            value={username}
            onChange={(e) => updateUsername(e.target.value)}
            name="username"
          />
          <button class="form-field" type="submit" onClick={handleOnSubmit}>
            Search
          </button>
        </div>
      ) : (
        <div class="form-container register-form">
          {submitted ? (
            <div class="success-message">
              Success! Thank you for registering
            </div>
          ) : null}
          <input
            value={user.username}
            id="user-name"
            class="form-field"
            type="text"
            placeholder="Username"
            name="username"
            onChange={getUserData}
          />
          {/* <span id="user-name-error">Please enter a first name</span> */}
          <input
            value={user.firstName}
            id="first-name"
            class="form-field"
            type="text"
            placeholder="First Name"
            name="firstName"
            onChange={getUserData}
          />
          {/* <span id="first-name-error">Please enter a last name</span> */}
          <input
            value={user.lastName}
            id="last-name"
            class="form-field"
            type="text"
            placeholder="Last Name"
            name="lastName"
            onChange={getUserData}
          />
          {/* <span id="last-error">Please enter an email address</span> */}

          <input
            value={user.dob}
            id="dob"
            class="form-field"
            type="text"
            placeholder="DOB"
            name="dob"
            onChange={getUserData}
          />
          <input
            value={user.phone}
            id="phone"
            class="form-field"
            type="number"
            placeholder="Phone"
            name="phone"
            onChange={getUserData}
          />
          <input
            value={user.email}
            id="email"
            class="form-field"
            type="email"
            placeholder="Email"
            name="email"
            onChange={getUserData}
          />
          <input
            value={user.address}
            id="address"
            class="form-field"
            type="text"
            placeholder="address"
            name="address"
            onChange={getUserData}
          />
          <input
            value={user.aadharNo}
            id="aadharNo"
            class="form-field"
            type="number"
            placeholder="Aadhar Number"
            name="aadharNo"
            onChange={getUserData}
          />
          <input
            value={user.panNo}
            id="panNo"
            class="form-field"
            type="text"
            placeholder="PAN number"
            name="panNo"
            onChange={getUserData}
          />
          <button class="form-field" type="submit" onClick={onClick}>
            Register
          </button>
        </div>
      )}
    </div>
  );
}
