export class BankDetails extends Component {
  async componentDidMount() {
    await this.getDetails();
  }

  async getDetails() {
    const web3 = window.web3;
    this.acc = web3.currentProvider.selectedAddress;
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    const kyc = new web3.eth.Contract(Kycabi.abi, networkData.address);
    let details = await kyc.methods
      .viewBank()
      .call({
        from: this.acc,
        gas: 4700000,
      })
      .then(function () {
        this.name = details[0];
        this.count = details[1];
      });
  }

  render() {
    return (
      <div
        style={{
          marginLeft: "20px",
        }}
      >
        <div>
          <h3>Bank name : {this.name}</h3>
        </div>
        <div>
          <h3>ETH Address : {window.web3.currentProvider.selectedAddress}</h3>
        </div>
        <div>
          <h3>Total KYC count : {this.count}</h3>
        </div>
      </div>
    );
  }
}
