import React, { Component } from "react";
import "./App.css";
import Web3 from "web3";
import Kycabi from "./contracts/kyc.json";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import BankHome from "./components/bank_home/bank_home";
import Navbar from "./components/navbar/Navbar";
import BankAuth from "./components/bank_auth/bank_auth";

class App extends Component {
  async componentDidMount() {
    await this.loadWeb3();
    await this.loadBlockchainData();
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum);
      await window.ethereum.enable();
    } else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider);
    } else {
      window.alert(
        "Non-Ethereum browser detected. You should consider trying MetaMask!"
      );
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3;
    const accounts = await web3.eth.getAccounts();
    this.setState({ account: accounts[0] });
    const networkId = await web3.eth.net.getId();
    const networkData = Kycabi.networks[networkId];
    if (networkData) {
      console.log("success");
      var acc = web3.currentProvider.selectedAddress;
      console.log(acc);
    } else {
      window.alert("Kyc contract not deployed to detected network.");
    }
  }

  render() {
    return (
      <div className="App">
        <BankAuth />
      </div>
    );
  }
}

export default App;
